		<script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js"></script>
		<script type="text/javascript">
		$(document).ready(function() {
			$("#formCompany").submit(function(e){
				e.preventDefault(e);
				
				$("#errorBusinessCont").empty();
				$.post("/api/company/new",  $("#formCompany").serialize(), function(data){
					if(data.error) {
						$.each( data.error, function( key, value ) {
							$('<span/>',{
								text: value[0],
								class: 'errorRow'
							}).appendTo('#errorBusinessCont');
						});
					}else{
						//update company list
						$("#CompaniesCont").html(data.companies);
						
						// only occurs when the application has no data
						@if( count($companies) === 0)
							//unhide block ontop of new employee IF there were no businesses to start
							$("#blockNewEmployees").hide();
						@endif
						
						//update the new employee container so the dropdown reflects the proper amount of companies
						$("#employeeCompanyDropdown").html(data.NewEmployees);
						//clear the new company field
						$( '#formCompany' ).each(function(){
								this.reset();
						});		
					}
				});
				
			});			
			
			$("#FormEmployee").submit(function(e){
				e.preventDefault(e);
				
				$("#errorEmployeeCont").empty();
				$.post("/api/employee/new",  $("#FormEmployee").serialize(), function(data){
					if(data.error){
						//if error - display error
						$.each( data.error, function( key, value ) {
							$('<span/>',{
								text: value[0],
								class: 'errorRow'
							}).appendTo('#errorEmployeeCont');
						});
					}else{
						//update company list
						$("#CompaniesCont").html(data.companies);
						//update employee list
						$("#employeeCont").html(data.employees);
						//clear the new company field
						$( '#FormEmployee' ).each(function(){
								this.reset();			
						});
					}
				});
				
			});
		});
		</script>
